root.script:
		$.Msg( "Compiled Panorama Scripts!" );
		$("#build_button_1").SetPanelEvent(
			"onmouseover",
  			function(){
				$.Msg( "Panorama onmouseover!" );
    			$.DispatchEvent("DOTAShowTextTooltip", $("#build_button_1"), "This is my tooltip!");
  			}
		);
		$("#build_button_1").SetPanelEvent(
			"onmouseout",
  			function(){
				$.Msg( "Panorama onmouseout!" );
    			$.DispatchEvent("DOTAHideTextTooltip");
  			}
		);
